import { StyledPlayerModal } from "./StyledPlayerModal";

import { useContext, useEffect, useState } from "react";
import { UserContext } from "contexts/UserContext";
import { Link, useNavigate, useParams } from "react-router-dom";
import { iPlayers } from "contexts/interfaces";
import disableScroll from "disable-scroll";

import closeModalIcon from "../../assets/close-modal-icon.svg";
import "animate.css";

export function PlayerModal(){

    const { players, getRouteIcon } = useContext(UserContext);
    const { userId } = useParams();
    const [user, setUser] = useState<iPlayers>({} as iPlayers);
    const navigate = useNavigate();


    function getUser(){
        const foundUser: iPlayers| undefined = players.find((pl)=>{
            return pl.id == userId
        })
        if(foundUser){
            setUser(foundUser)
        }
    }

    useEffect(()=>{
        getUser();
    })

    disableScroll.on()

    return(
        <StyledPlayerModal mainSplashArt={user.main?.splashart}>
            <div className="animate__animated animate__fadeInUp">
                <div className="modal-container">
                    <header>
                        <figure>
                            <img src={user.profileIcon || "data:image/jpeg;base64,"} alt={user.nickname} />
                        </figure>
                        <h2>{user.nickname}</h2>

                        <Link to="/players">
                            <img src={closeModalIcon}></img>
                        </Link>
                    </header>
                    <div className="user-info">
                        <h3>Elo: <span>{user.elo}</span></h3>
                        {
                            user.main &&
                            <h3>Main: <span>{user.main?.name}</span></h3>
                        }
                        <div className="user-route">
                            <h3>Rota Preferida: <span>{user.route}</span></h3>
                            <img src={getRouteIcon(user.route)} alt="" />
                        </div>
                        {
                            user.discord &&
                            <h3>Discord: <span>{user.discord}</span></h3>
                        }
                        {
                            user.bio &&
                            <p>
                                <span>Bio: </span>
                                {user.bio}
                            </p>
                        }
                            <a target="_blank" href={`https://www.leagueofgraphs.com/pt/summoner/br/${user.nickname?.replace(" ","+")}`}>
                                Ver hist√≥rico de partidas
                            </a>
                    </div>
                </div>
            </div>
        </StyledPlayerModal>
    )

}